package com.example.authservice.controller; import org.springframework.web.bind.annotation.*; import org.springframework.http.ResponseEntity; import java.util.Map; @RestController @RequestMapping("/auth") public class AuthController { @PostMapping("/register") public ResponseEntity<?> register(@RequestBody Map<String,String> body){ return ResponseEntity.ok(Map.of("msg","ok-register")); } @PostMapping("/login") public ResponseEntity<?> login(@RequestBody Map<String,String> body){ return ResponseEntity.ok(Map.of("token","mock-token")); } }