package com.example.riskservice.controller; import org.springframework.web.bind.annotation.*; import java.util.Map; @RestController public class RiskController { @PostMapping("/evaluate") public Map<String,Object> eval(@RequestBody Map<String,Object> body){ String doc = String.valueOf(body.get("documento")); int score = Math.abs(doc.hashCode())%651 + 300; String nivel = score<=500? "ALTO": score<=700? "MEDIO":"BAJO"; return Map.of("documento",doc,"score",score,"nivelRiesgo",nivel); } }